module.exports = function(req, res){
        var fs = require('fs');
        filePath = 'static/' + req.params;
        fs.readFile(filePath, function(err, file){
            if(err){
                res.send('404 Not Found', 404);
                return;
            }
            res.contentType(filePath);
            res.send(file);
        });
};
